import React, { useEffect, useRef, useState } from 'react';
import './Feature.css';
import 'animate.css';

export default function Feature() {
  const [isAppointmentExpanded, setIsAppointmentExpanded] = useState(false);
  const [isVoiceExpanded, setIsVoiceExpanded] = useState(false);
  const [isBillingExpanded, setIsBillingExpanded] = useState(false);
  const [imageSrc, setImageSrc] = useState("https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-1-1-1010x1024.webp");

  const [isInView, setIsInView] = useState(false);
  const featureRef = useRef(null);

  // Scroll animation handler
  useEffect(() => {
    const handleScroll = () => {
      const topPos = featureRef.current?.getBoundingClientRect().top;
      const windowHeight = window.innerHeight;
      if (topPos < windowHeight - 100) {
        setIsInView(true);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const handleAppointmentToggle = () => {
    setIsAppointmentExpanded(!isAppointmentExpanded);
    setImageSrc(
      isAppointmentExpanded ? "https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-1-1-1010x1024.webp" : "https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-1-1-1010x1024.webp"
    );
    setIsVoiceExpanded(false);
    setIsBillingExpanded(false);
  };

  const handleVoiceToggle = () => {
    setIsVoiceExpanded(!isVoiceExpanded);
    setImageSrc(
      isVoiceExpanded ? "https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-1-1-1010x1024.webp" : "https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-1-2-1010x1024.webp"
    );
    setIsAppointmentExpanded(false);
    setIsBillingExpanded(false);
  };

  const handleBillingToggle = () => {
    setIsBillingExpanded(!isBillingExpanded);
    setImageSrc(
      isBillingExpanded ? "https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-1-1-1010x1024.webp" : "https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-1-3-1010x1024.webp"
    );
    setIsAppointmentExpanded(false);
    setIsVoiceExpanded(false);
  };

  return (
    <div ref={featureRef}>
      <div className="row">
        {/* Left Column */}
        <div className={`col-md-5 text-center text-md-start ${isInView ? 'animate__animated animate__fadeInLeft' : ''}`}>
          <img src={imageSrc} alt="Healthcare" height="600" />
        </div>

        {/* Right Column */}
        <div className={`col-md-7 text-center text-md-start ${isInView ? 'animate__animated animate__fadeInRight' : ''}`}>
          <div className="fs-1 fw-bold" style={{ marginTop: '50px', fontFamily: 'Roboto' }}>
            Boost healthcare support <br /> efficiency with human-like <br /> empathy
          </div>
          <div>
            Make high query volume handling effortless: Self-serve 90% of FAQs with accurate <br /> resolutions (having 1% hallucinations). Personalize interactions in 135+ languages.
          </div>

          {/* Collapsible Sections */}
          <div className="row" style={{ marginTop: '50px' }}>
            <button className="btn btn-link p-0 text-start text-dark toggle-button" onClick={handleAppointmentToggle}>
              <div className="fw-bold col-sm-8" style={{ fontSize: '20px', fontFamily: 'inherit' }}>Effortless appointment booking</div>
              <div className="col-sm-2">
                <img
                  src={isAppointmentExpanded ? "https://cdn-icons-png.flaticon.com/512/262/262039.png" : "https://cdn-icons-png.flaticon.com/128/3024/3024515.png"}
                  alt={isAppointmentExpanded ? "Minus icon" : "Plus icon"}
                  className="toggle-icon"
                />
              </div>
            </button>
            <div className={`collapse-content ${isAppointmentExpanded ? 'animate__animated animate__fadeIn show' : 'animate__animated animate__fadeOut hide'} text-start`}>
              Easily book appointments, send reminders, and notify patients with our integrated appointment management system.
            </div>
          </div>

          <div className="row" style={{ marginTop: '50px' }}>
            <button className="btn btn-link p-0 text-start text-dark toggle-button" onClick={handleVoiceToggle}>
              <div className="fw-bold col-sm-8" style={{ fontSize: '20px', fontFamily: 'inherit' }}>Human-like voice interactions</div>
              <div className="col-sm-2">
                <img
                  src={isVoiceExpanded ? "https://cdn-icons-png.flaticon.com/512/262/262039.png" : "https://cdn-icons-png.flaticon.com/128/3024/3024515.png"}
                  alt={isVoiceExpanded ? "Minus icon" : "Plus icon"}
                  className="toggle-icon"
                />
              </div>
            </button>
            <div className={`collapse-content ${isVoiceExpanded ? 'animate__animated animate__fadeIn show' : 'animate__animated animate__fadeOut hide'} text-start`}>
              Tackle interruptions with empathy using voice AI agents, adept in active listening. Transfer complex queries to live agents.
            </div>
          </div>

          <div className="row" style={{ marginTop: '50px' }}>
            <button className="btn btn-link p-0 text-start text-dark toggle-button" onClick={handleBillingToggle}>
              <div className="fw-bold col-sm-8" style={{ fontSize: '20px', fontFamily: 'inherit' }}>Secure billing and payments</div>
              <div className="col-sm-2">
                <img
                  src={isBillingExpanded ? "https://cdn-icons-png.flaticon.com/512/262/262039.png" : "https://cdn-icons-png.flaticon.com/128/3024/3024515.png"}
                  alt={isBillingExpanded ? "Minus icon" : "Plus icon"}
                  className="toggle-icon"
                />
              </div>
            </button>
            <div className={`collapse-content ${isBillingExpanded ? 'animate__animated animate__fadeIn show' : 'animate__animated animate__fadeOut hide'} text-start`}>
              Streamline invoicing, billing, and reimbursements with smooth integration with existing payment solutions.
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
