import React, {useEffect,useState} from 'react';

export default function Feature() {

  const [isSecurityExpanded, setIsSecurityExpanded] = useState();
  const [isAlertExpended, setIsAlertExpended] = useState();
  const [imageSrc, setImageSrc] = useState("https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-1-1-1010x1024.webp");


  const handlesecurityToggle = () => {
    setIsSecurityExpanded(!isSecurityExpanded);
    if (!isSecurityExpanded) {
      setImageSrc("https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-1-1-1010x1024.webp");
    }
    if (isAlertExpended || isBillingExpand) {
      setIsVoiceExpanded(false);
      setIsBillingExpanded(false);
    }
  };
  const handleAlertsToggle = () => {
    setIsAlertExpended(!isAlertExpended);
    if(!isAlertExpended) {
      setImageSrc("https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-2-2-1010x1024.webp")
    }

  }



  return (
    <div className="container-fluid">
      <div className="row" style={{ background: '#fff6d7', margin: '0' }}>
        {/* Text Column */}
        <div className="col-md-8 d-flex align-items-center justify-content-center p-4">
          <div className="text-center">
            <div className="row text-start fs-2 fw-bold" style={{ fontFamily: 'roboto' }}>
              <h2>Win patient loyalty with <br /> connected healthcare <br /> journeys</h2>
            </div>
            <div className="row text-start">
              <p>Prevent patient churns. Help them find nearby clinics, book virtual consultations, <br /> and enable post-consultation follow-ups, capturing context across 35+ channels.</p>
            </div>
           <div className="row"> 
            <button 
                  className="btn btn-link p-0 text-start text-dark fw-bold toggle-button" 
                  onClick={handlesecurityToggle}
                >
                 Enterprise-grade security
                  <img 
                    src={isSecurityExpand ? "https://cdn-icons-png.flaticon.com/512/262/262039.png" : "https://cdn-icons-png.flaticon.com/128/3024/3024515.png"} 
                    alt={isSecurityExpand ? "Minus icon" : "Plus icon"} 
                    className="toggle-icon"
                  />
                </button>
                </div>
                <div className="row"> 
            <button 
                  className="btn btn-link p-0 text-start text-dark fw-bold toggle-button" 
                  onClick={handleAlertsToggle}
                >
                 Proactive alerts and suggestions
                  <img 
                    src={isSecurityExpand ? "https://cdn-icons-png.flaticon.com/512/262/262039.png" : "https://cdn-icons-png.flaticon.com/128/3024/3024515.png"} 
                    alt={isSecurityExpand ? "Minus icon" : "Plus icon"} 
                    className="toggle-icon"
                  />
                </button>
                </div>
          </div>
        </div>

        {/* Image Column */}
        <div className="col-md-4 p-0 d-flex justify-content-center align-items-center">
          <img
            src="https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-2-1-1010x1024.webp"
            alt="Healthcare journey illustration"
            style={{ width: '100%', height: 'auto', maxWidth: '100%', marginTop: '50px' }}
          />
        </div>
      </div>
    </div>
  );
}
