import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Feature1.css';

export default function Feature1() {
  const [activeIndex, setActiveIndex] = useState(null);

  const images = [
    'https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-2-1-1010x1024.webp',
    'https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-2-2-1010x1024.webp',
    'https://yellow.ai/wp-content/uploads/2024/06/healthcare-inline-img-2-3-1010x1024.webp',
  ];

  const faqs = [
    {
      question: 'What is healthcare support efficiency?',
      answer: 'Healthcare support efficiency refers to the ability to manage and resolve patient inquiries and issues effectively and swiftly.'
    },
    {
      question: 'How can self-service reduce query volume?',
      answer: 'Self-service allows patients to find answers to common questions on their own, significantly decreasing the number of inquiries handled by support staff.'
    },
    {
      question: 'What languages are supported?',
      answer: 'Our system supports over 135 languages to ensure personalized interactions for all users.'
    },
  ];

  const toggleText = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div className="container-fluid p-0 m-0" style={{ maxHeight: '80vh', backgroundColor: '#A5B4FC' }}>
      <div className="row g-0">
        {/* Second column: Image (switched for small screens) */}
        <div className="col-md-6 col-sm-12 p-0">
          <div className="col-md-8 p-0 d-flex justify-content-center align-items-center" style={{ height: '80vh' }}>
            <img 
              src={activeIndex !== null ? images[activeIndex] : images[0]} 
              alt="Feature"
              style={{ width: '100%', height: 'auto', maxWidth: '100%', marginTop: '50px' }}
            />
          </div>
        </div>

        {/* First column: Text */}
        <div className="col-md-6 col-sm-12 p-0" style={{ marginTop: '100px' }}>
          <div className="row ">
            <div className="col-sm-8">
              <h2 className='left-column'>Boost healthcare support efficiency with human-like empathy</h2>
              <p className="mt4 left-column">Make high query volume handling effortless: Self-serve 90% of FAQs with accurate resolutions (having 1% hallucinations). Personalize interactions in 135+ languages.</p>
            </div>
          </div>
          <div className="row">
            <div className="col-sm-8">
              {faqs.map((faq, index) => (
                <div key={index}>
                  <button 
                    className="row align-items-center btn my-4 text-start fs-4 fw-bold faq-question left-column" 
                    onClick={() => toggleText(index)}
                  >
                    <div className="d-flex align-items-center">
                      <img
                        src={activeIndex === index 
                          ? 'https://cdn-icons-png.flaticon.com/512/262/262039.png' 
                          : 'https://cdn-icons-png.flaticon.com/128/3024/3024515.png'}
                        alt={activeIndex === index ? 'Minus icon' : 'Plus icon'}
                        style={{ width: '24px', height: '24px', marginRight: '10px' }}
                      />
                      <span className="question" style={{ background: 'none' }}>{faq.question}</span>
                    </div>
                  </button>
                  {activeIndex === index && (
                    <p className="col-sm-10 faq-answer left-column">{faq.answer}</p>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
