import React, { useState } from 'react';
import './Feature.css'; // Import a CSS file for styling

export default function Feature() {
  const [activeIndex, setActiveIndex] = useState(null);

  const images = [
    'image-url-1.jpg',
    'image-url-2.jpg',
    'image-url-3.jpg',
    'image-url-4.jpg',
  ];

  const toggleText = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div className="feature-container">
      <div className="row">
        <div className="col-sm-12">
        <div className="col-sm-6">
          <img src={activeIndex !== null ? images[activeIndex] : images[0]} alt="Feature" />
        </div>
        <div className="col-sm-6">
          <h2>Your Heading Here</h2>
          <p>Your paragraph text goes here.</p>
          {images.map((_, index) => (
            <div key={index}>
              <button onClick={() => toggleText(index)}>
                Toggle FAQ {index + 1}
              </button>
              {activeIndex === index && (
                <p>FAQ question {index + 1} answer goes here.</p>
              )}
            </div>
          ))}
        </div>
        </div>
      </div>
    </div>
  );
}
